$(document).ready(function(){$(".api__notifications__delete").on("click",function(){var e=$(this),t=e.data("id"),n=$('[name="csrfmiddlewaretoken"]').attr("value");$.ajax({url:`/api/notifications/${t}/`,type:"DELETE",headers:{"X-CSRFToken":n},success:function(){e.parent("li").remove(),console.log("successfully removed")},error:function(e){alert(`Error: ${e.status}`)}})})});
